package se.mattec.advent2022

fun main(args: Array<String>) {
    println(Day6.problem1())
    println(Day6.problem2())
}

object Day6 {

    fun problem1(): String {
        val charList = data.toList()
        for (i in 0 until charList.size - 4) {
            if (charList.subList(i, i + 4).toSet().size == 4) {
                return (i + 4).toString()
            }
        }
        throw IllegalStateException("No solution.")
    }

    fun problem2(): String {
        val charList = data.toList()
        for (i in 0 until charList.size - 14) {
            if (charList.subList(i, i + 14).toSet().size == 14) {
                return (i + 14).toString()
            }
        }
        throw IllegalStateException("No solution.")
    }

    private val data = """

    """.trimIndent()

}